import React from 'react';
import './App.css';

import legend from "./LegendData"
import Rect from "./LegendRect.js"

function App() {

  let Legend = legend.map((d, i) => {
  	console.log(d)
  	return <Rect
      key = {d.id}
      color = {d.color}
      x = '10'
      y = {i * 22}
      width = {20}
      height = {20}
    />
  })

  let Text = legend.map((d, i) => {
    return <Label
      key={d.id}
      x='30'
      y={i * 30}
      category = {d.category}
    />

  })

  return (
    <svg>
      {Legend}{Text}
    </svg>
  );
}

function Label(props) {
  return (
    <text x={props.x} y={props.y}>{props.category}</text>
  )
}



export default App;
