import React from 'react';
import './App.css';

import legend from "./LegendData"

function App() {
  const width = 200
  const height = 180

  return (
    <svg id = 'chartLegend' width = {width} height = {height}>
      {Rects}{Labels}
    </svg>
  );
}

let Labels = legend.map((d, i) => {
  return <Label
    key={d.id}
    x='35'
    y={(i + 1) * 22 - 4}
    category={d.category}
  />
})

function Rects(props) {
  return (
    <rect
      width={props.width}
      height={props.height}
      id={props.id}
      fill={props.color}
      x='10'
      y={props.y}>
    </rect>
  )
}

function Circle(props) {
  return (
    <circle
      cx={props.x}
      cy={props.height}
      id={props.id}
      fill={props.color}
      r='10'>
    </circle>
  )
}

function Label(props) {
  return (
    <text
      x={props.x}
      y={props.y}>
        {props.category}
    </text>
  )
}

export default App;
