import React from 'react';
import './App.css';

import legend from "./LegendData"

function App() {
  const width = 200
  const height = 200

  let Rects = legend.map((d, i) => {
  	return <Rect
      key = {i}
      id = {i}
      color = {d.color}
      x = '10'
      y = {i * 22}
      width = {20}
      height = {20}
    />
  })

  let Labels = legend.map((d, i) => {
    return <Label
      key={d.id}
      x='35'
      y={(i + 1) * 22 - 4}
      category = {d.category}
    />
  })

  return (
    <svg id = 'chartLegend' width = {width} height = {height}>
      {Rects}{Labels}
    </svg>
  );
}

function Rect(props) {
  return (
    <rect
      width={props.width}
      height={props.height}
      id={props.id}
      fill={props.color}
      x='10'
      y={props.y}>
    </rect>
  )
}

function Label(props) {
  return (
    <text
      x={props.x}
      y={props.y}>
        {props.category}
    </text>
  )
}

export default App;
